import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { courseAPI } from '../services/api';

const Dashboard = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', description: '' });
  const [formLoading, setFormLoading] = useState(false);

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await courseAPI.getAll();
      setCourses(response.data.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormLoading(true);

    try {
      const response = await courseAPI.create(formData);
      setCourses([...courses, response.data.data]);
      setFormData({ name: '', description: '' });
      setShowForm(false);
    } catch (error) {
      console.error('Error creating course:', error);
    } finally {
      setFormLoading(false);
    }
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course? All notes will be deleted as well.')) {
      try {
        await courseAPI.delete(courseId);
        setCourses(courses.filter(course => course.id !== courseId));
      } catch (error) {
        console.error('Error deleting course:', error);
      }
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading"></div> Loading courses...
      </div>
    );
  }

  return (
    <div className="container">
      <div className="header">
        <h1>StudyBuddy</h1>
      </div>

      <div className="page-title">My Courses</div>

      {showForm && (
        <div className="form-container">
          <h2 className="form-title">Create New Course</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Course Name *</label>
              <input
                type="text"
                className="form-input"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                className="form-textarea"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                rows="3"
              />
            </div>
            <div className="actions">
              <button type="submit" className="btn" disabled={formLoading}>
                {formLoading ? <div className="loading"></div> : 'Create Course'}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => setShowForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {!showForm && (
        <button className="btn" onClick={() => setShowForm(true)}>
          + Add New Course
        </button>
      )}

      {courses.length === 0 ? (
        <div className="empty-state">
          <h3>No courses yet</h3>
          <p>Create your first course to get started with organizing your study notes.</p>
        </div>
      ) : (
        <div className="course-list">
          {courses.map((course) => (
            <div key={course.id} className="course-card">
              <Link to={`/course/${course.id}`} style={{ textDecoration: 'none' }}>
                <div className="course-title">{course.name}</div>
                {course.description && (
                  <div className="course-description">{course.description}</div>
                )}
              </Link>
              <div className="course-meta">
                <span>Created {new Date(course.created_at).toLocaleDateString()}</span>
                <button 
                  className="btn btn-danger" 
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(course.id);
                  }}
                  style={{ fontSize: '12px', padding: '4px 8px' }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
