import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import { noteAPI } from '../services/api';

const NoteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [note, setNote] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({ title: '', body: '' });
  const [saving, setSaving] = useState(false);
  const [summarizing, setSummarizing] = useState(false);
  const [course, setCourse] = useState(null);

  useEffect(() => {
    fetchNote();
  }, [id]);

  const fetchNote = async () => {
    try {
      const response = await noteAPI.getById(id);
      const noteData = response.data.data;
      setNote(noteData);
      setFormData({ title: noteData.title, body: noteData.body || '' });

      // Fetch course info for breadcrumb
      if (noteData.course_id) {
        const courseResponse = await fetch(`/api/courses/${noteData.course_id}`);
        if (courseResponse.ok) {
          const courseData = await courseResponse.json();
          setCourse(courseData.data);
        }
      }
    } catch (error) {
      console.error('Error fetching note:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      const response = await noteAPI.update(id, formData);
      setNote(response.data.data);
      setEditing(false);
    } catch (error) {
      console.error('Error saving note:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleSummarize = async () => {
    setSummarizing(true);
    try {
      const response = await noteAPI.summarize(id);
      setNote(response.data.data.note);
    } catch (error) {
      console.error('Error summarizing note:', error);
      alert('Failed to generate summary. Please check your API key and try again.');
    } finally {
      setSummarizing(false);
    }
  };

  const handleCancel = () => {
    setFormData({ title: note.title, body: note.body || '' });
    setEditing(false);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading"></div> Loading note...
      </div>
    );
  }

  if (!note) {
    return (
      <div className="container">
        <div className="empty-state">
          <h3>Note not found</h3>
          <Link to="/" className="btn">Back to Dashboard</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="header">
        <h1>StudyBuddy</h1>
      </div>

      <nav className="breadcrumb">
        <span className="breadcrumb-item">
          <Link to="/">Dashboard</Link>
        </span>
        {course && (
          <span className="breadcrumb-item">
            <Link to={`/course/${course.id}`}>{course.name}</Link>
          </span>
        )}
        <span className="breadcrumb-item active">{note.title}</span>
      </nav>

      <div className="note-editor">
        <div className="editor-header">
          <h2 className="page-title">Note Details</h2>
          <div className="editor-actions">
            {!editing && (
              <>
                <button className="btn" onClick={() => setEditing(true)}>
                  Edit
                </button>
                <button 
                  className="btn btn-success" 
                  onClick={handleSummarize}
                  disabled={summarizing || !note.body}
                >
                  {summarizing ? (
                    <>
                      <div className="loading"></div> Summarizing...
                    </>
                  ) : (
                    'üìù Summarize'
                  )}
                </button>
              </>
            )}
            {editing && (
              <>
                <button 
                  className="btn" 
                  onClick={handleSave}
                  disabled={saving}
                >
                  {saving ? <div className="loading"></div> : 'Save'}
                </button>
                <button className="btn btn-secondary" onClick={handleCancel}>
                  Cancel
                </button>
              </>
            )}
          </div>
        </div>

        <div className="form-group">
          <label className="form-label">Title</label>
          {editing ? (
            <input
              type="text"
              className="form-input"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            />
          ) : (
            <div style={{ fontSize: '1.2rem', fontWeight: '600', marginBottom: '10px' }}>
              {note.title}
            </div>
          )}
        </div>

        <div className="form-group">
          <label className="form-label">Content</label>
          {editing ? (
            <textarea
              className="form-textarea"
              value={formData.body}
              onChange={(e) => setFormData({ ...formData, body: e.target.value })}
              rows="12"
              placeholder="Enter your study notes here..."
            />
          ) : (
            <div className="card">
              <pre style={{ 
                whiteSpace: 'pre-wrap', 
                fontFamily: 'inherit', 
                margin: 0,
                lineHeight: '1.6'
              }}>
                {note.body || 'No content yet. Click Edit to add content.'}
              </pre>
            </div>
          )}
        </div>

        {note.summary && (
          <div className="summary-card">
            <div className="summary-title">üìù AI Summary</div>
            <div 
              className="summary-content"
              dangerouslySetInnerHTML={{ 
                __html: note.summary.replace(/\n/g, '<br>').replace(/\* (.+)/g, '<li>$1</li>').replace(/(<li>.*<\/li>)/s, '<ul>$1</ul>') 
              }}
            />
          </div>
        )}

        <div style={{ marginTop: '20px', fontSize: '0.85rem', color: '#666' }}>
          Created: {new Date(note.created_at).toLocaleString()}<br />
          Last updated: {new Date(note.updated_at).toLocaleString()}
        </div>
      </div>
    </div>
  );
};

export default NoteDetail;
