import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { courseAPI, noteAPI } from '../services/api';

const CourseDetail = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [notes, setNotes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ title: '', body: '' });
  const [selectedFile, setSelectedFile] = useState(null);
  const [formLoading, setFormLoading] = useState(false);

  useEffect(() => {
    fetchCourseData();
  }, [id]);

  const fetchCourseData = async () => {
    try {
      const [courseResponse, notesResponse] = await Promise.all([
        courseAPI.getById(id),
        noteAPI.getByCourse(id)
      ]);
      setCourse(courseResponse.data.data);
      setNotes(notesResponse.data.data);
    } catch (error) {
      console.error('Error fetching course data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormLoading(true);

    try {
      const response = await noteAPI.create(id, formData, selectedFile);
      setNotes([...notes, response.data.data]);
      setFormData({ title: '', body: '' });
      setSelectedFile(null);
      setShowForm(false);
    } catch (error) {
      console.error('Error creating note:', error);
    } finally {
      setFormLoading(false);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.type === 'application/pdf') {
        setSelectedFile(file);
      } else {
        alert('Please select a PDF file only.');
        e.target.value = '';
      }
    }
  };

  const handleDeleteNote = async (noteId) => {
    if (window.confirm('Are you sure you want to delete this note?')) {
      try {
        await noteAPI.delete(noteId);
        setNotes(notes.filter(note => note.id !== noteId));
      } catch (error) {
        console.error('Error deleting note:', error);
      }
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading"></div> Loading course...
      </div>
    );
  }

  if (!course) {
    return (
      <div className="container">
        <div className="empty-state">
          <h3>Course not found</h3>
          <Link to="/" className="btn">Back to Dashboard</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="header">
        <h1>StudyBuddy</h1>
      </div>

      <nav className="breadcrumb">
        <span className="breadcrumb-item">
          <Link to="/">Dashboard</Link>
        </span>
        <span className="breadcrumb-item active">{course.name}</span>
      </nav>

      <div className="page-title">{course.name}</div>

      {course.description && (
        <div className="card">
          <p>{course.description}</p>
        </div>
      )}

      {showForm && (
        <div className="form-container">
          <h2 className="form-title">Create New Note</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Note Title *</label>
              <input
                type="text"
                className="form-input"
                value={formData.title}
                onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                required
              />
            </div>
            <div className="form-group">
              <label className="form-label">Note Content</label>
              <textarea
                className="form-textarea"
                value={formData.body}
                onChange={(e) => setFormData({ ...formData, body: e.target.value })}
                rows="6"
                placeholder="Enter your study notes here..."
              />
            </div>
            <div className="form-group">
              <label className="form-label">PDF File (Optional)</label>
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                style={{ marginBottom: '10px' }}
              />
              {selectedFile && (
                <div style={{ fontSize: '0.9rem', color: '#666' }}>
                  Selected: {selectedFile.name} ({(selectedFile.size / 1024 / 1024).toFixed(2)} MB)
                </div>
              )}
            </div>
            <div className="actions">
              <button type="submit" className="btn" disabled={formLoading}>
                {formLoading ? <div className="loading"></div> : 'Create Note'}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => setShowForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {!showForm && (
        <button className="btn" onClick={() => setShowForm(true)}>
          + Add New Note
        </button>
      )}

      {notes.length === 0 ? (
        <div className="empty-state">
          <h3>No notes yet</h3>
          <p>Create your first note for this course to get started.</p>
        </div>
      ) : (
        <div className="note-list">
          {notes.map((note) => (
            <div key={note.id} className="note-card">
              <Link to={`/note/${note.id}`} style={{ textDecoration: 'none' }}>
                <div className="note-title">{note.title}</div>
                {note.body && (
                  <div className="note-preview">
                    {note.body.substring(0, 150)}
                    {note.body.length > 150 && '...'}
                  </div>
                )}
              </Link>
              <div className="note-meta">
                <span>Updated {new Date(note.updated_at).toLocaleDateString()}</span>
                {note.summary && <span>üìù Has Summary</span>}
              </div>
              <div className="actions">
                <button 
                  className="btn btn-danger" 
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeleteNote(note.id);
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CourseDetail;
