import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { courseAPI, noteAPI } from '../services/api';

const Dashboard = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', description: '' });
  const [formLoading, setFormLoading] = useState(false);
  const [notification, setNotification] = useState({ show: false, type: '', message: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    fetchCourses();
  }, []);

  const showNotification = (type, message) => {
    setNotification({ show: true, type, message });
    setTimeout(() => {
      setNotification({ show: false, type: '', message: '' });
    }, 3000);
  };

  const fetchCourses = async () => {
    try {
      const response = await courseAPI.getAll();
      setCourses(response.data.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
      showNotification('error', 'Failed to load courses');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormLoading(true);

    try {
      const response = await courseAPI.create(formData);
      if (response.data.success) {
        setCourses([...courses, response.data.data]);
        setFormData({ name: '', description: '' });
        setShowForm(false);
        showNotification('success', 'Course created successfully!');
      } else {
        showNotification('error', `Error creating course: ${response.data.error}`);
      }
    } catch (error) {
      console.error('Error creating course:', error);
      showNotification('error', 'Failed to create course. Please try again.');
    } finally {
      setFormLoading(false);
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course and all its notes?')) {
      try {
        await courseAPI.delete(courseId);
        setCourses(courses.filter(course => course.id !== courseId));
        showNotification('success', 'Course deleted successfully!');
      } catch (error) {
        console.error('Error deleting course:', error);
        showNotification('error', 'Failed to delete course');
      }
    }
  };

  const getCourseIcon = (courseName) => {
    const icons = ['ğŸ“š', 'ğŸ“–', 'ï¿½', 'ï¿½', 'ï¿½', 'ï¿½', 'ğŸ“”', 'ï¿½', 'ğŸ“—'];
    const index = courseName ? courseName.charCodeAt(0) % icons.length : 0;
    return icons[index];
  };

  const filteredAndSortedCourses = courses
    .filter(course => 
      course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (course.description && course.description.toLowerCase().includes(searchTerm.toLowerCase()))
    )
    .sort((a, b) => {
      switch (sortBy) {
        case 'newest':
          return new Date(b.created_at) - new Date(a.created_at);
        case 'oldest':
          return new Date(a.created_at) - new Date(b.created_at);
        case 'name':
          return a.name.localeCompare(b.name);
        default:
          return 0;
      }
    });

  const totalNotes = courses.reduce((sum, course) => sum + (course.note_count || 0), 0);
  const totalSummaries = courses.reduce((sum, course) => sum + (course.summary_count || 0), 0);

  const getNoteCount = (courseId) => {
    // This would typically come from the API, but for now we'll use a placeholder
    return Math.floor(Math.random() * 10) + 1;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="header">
          <h1>StudyBuddy</h1>
          <div className="subtitle">Your AI-powered study organization tool</div>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '400px' }}>
          <div className="loading loading-lg"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="container">
        <div className="header">
          <h1>StudyBuddy</h1>
          <div className="subtitle">Your AI-powered study organization tool</div>
        </div>

        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-number">{courses.length}</div>
            <div className="stat-label">Total Courses</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{courses.reduce((acc, course) => acc + getNoteCount(course.id), 0)}</div>
            <div className="stat-label">Total Notes</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{courses.filter(course => getNoteCount(course.id) > 0).length}</div>
            <div className="stat-label">Active Courses</div>
          </div>
        </div>

        <div className="page-title">My Courses</div>

        {showForm && (
          <div className="form-container fade-in">
            <div className="form-title">
              <div className="form-icon">â•</div>
              Create New Course
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Course Name *</label>
                <div className="input-group">
                  <input
                    type="text"
                    className="form-input"
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    placeholder="e.g., Computer Science 101"
                    required
                  />
                </div>
              </div>
              <div className="form-group">
                <label className="form-label">Description</label>
                <textarea
                  className="form-textarea"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  rows="3"
                  placeholder="Brief description of the course..."
                />
              </div>
              <div className="actions">
                <button type="submit" className="btn btn-lg" disabled={formLoading}>
                  {formLoading ? <div className="loading"></div> : 'Create Course'}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary btn-lg" 
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {!showForm && (
          <button className="btn btn-lg" onClick={() => setShowForm(true)}>
            <span>â•</span> Add New Course
          </button>
        )}

        {courses.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">ğŸ“š</div>
            <h3>No courses yet</h3>
            <p>Start organizing your study materials by creating your first course.</p>
            <button className="btn btn-lg" onClick={() => setShowForm(true)}>
              Create Your First Course
            </button>
          </div>
        ) : (
          <div className="course-list">
            {courses.map((course, index) => (
              <div key={course.id} className="course-card fade-in" style={{ animationDelay: `${index * 0.1}s` }}>
                <Link to={`/course/${course.id}`} style={{ textDecoration: 'none' }}>
                  <div className="course-title">
                    <div className="course-icon">{getCourseIcon(course.name)}</div>
                    {course.name}
                  </div>
                  {course.description && (
                    <div className="course-description">{course.description}</div>
                  )}
                </Link>
                <div className="course-meta">
                  <div className="course-stats">
                    <div className="stat-item">
                      <span>ğŸ“</span>
                      <span>{getNoteCount(course.id)} notes</span>
                    </div>
                    <div className="stat-item">
                      <span>ğŸ“…</span>
                      <span>{new Date(course.created_at).toLocaleDateString()}</span>
                    </div>
                  </div>
                  <button 
                    className="btn btn-danger btn-sm" 
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteCourse(course.id);
                    }}
                  >
                    ğŸ—‘ï¸ Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Search and Sort Controls */}
        <div className="card fade-in" style={{ marginBottom: '1.5rem' }}>
          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', flexWrap: 'wrap' }}>
            <div style={{ flex: 1, minWidth: '200px' }}>
              <input
                type="text"
                placeholder="ğŸ” Search courses..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem 1rem',
                  border: '1px solid var(--border)',
                  borderRadius: '8px',
                  fontSize: '0.9rem'
                }}
              />
            </div>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              style={{
                padding: '0.75rem 1rem',
                border: '1px solid var(--border)',
                borderRadius: '8px',
                fontSize: '0.9rem',
                backgroundColor: 'var(--background)',
                color: 'var(--text-primary)'
              }}
            >
              <option value="newest">ğŸ“… Newest First</option>
              <option value="oldest">ğŸ“… Oldest First</option>
              <option value="name">ğŸ”¤ Sort by Name</option>
            </select>
          </div>
        </div>

        {/* Stats Overview */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-number">{courses.length}</div>
            <div className="stat-label">Total Courses</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{totalNotes}</div>
            <div className="stat-label">Total Notes</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{totalSummaries}</div>
            <div className="stat-label">AI Summaries</div>
          </div>
        </div>

        {filteredAndSortedCourses.length === 0 && courses.length > 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">ğŸ”</div>
            <h3>No courses found</h3>
            <p>Try adjusting your search terms or filters.</p>
            <button className="btn btn-secondary" onClick={() => setSearchTerm('')}>
              Clear Search
            </button>
          </div>
        ) : filteredAndSortedCourses.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-icon">ğŸ“š</div>
            <h3>No courses yet</h3>
            <p>Start organizing your study materials by creating your first course.</p>
            <button className="btn btn-lg" onClick={() => setShowForm(true)}>
              Create Your First Course
            </button>
          </div>
        ) : (
          <div className="course-list">
            {filteredAndSortedCourses.map((course, index) => (
              <div key={course.id} className="course-card fade-in" style={{ animationDelay: `${index * 0.1}s` }}>
                <Link to={`/course/${course.id}`} style={{ textDecoration: 'none' }}>
                  <div className="course-title">
                    <div className="course-icon">{getCourseIcon(course.name)}</div>
                    {course.name}
                  </div>
                  {course.description && (
                    <div className="course-description">{course.description}</div>
                  )}
                </Link>
                <div className="course-meta">
                  <div className="course-stats">
                    <div className="stat-item">
                      <span>ğŸ“</span>
                      <span>{getNoteCount(course.id)} notes</span>
                    </div>
                    <div className="stat-item">
                      <span>ğŸ“…</span>
                      <span>{new Date(course.created_at).toLocaleDateString()}</span>
                    </div>
                  </div>
                  <button 
                    className="btn btn-danger btn-sm" 
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteCourse(course.id);
                    }}
                  >
                    ğŸ—‘ï¸ Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {!showForm && courses.length > 0 && (
          <button className="floating-btn" onClick={() => setShowForm(true)} title="Add New Course">
            â•
          </button>
        )}

        {/* Notification Toast */}
        {notification.show && (
          <div 
            className={`notification ${notification.type}`}
            style={{
              position: 'fixed',
              top: '20px',
              right: '20px',
              padding: '1rem 1.5rem',
              borderRadius: '8px',
              color: 'white',
              fontWeight: '500',
              zIndex: 1000,
              animation: 'slideIn 0.3s ease-out',
              backgroundColor: notification.type === 'success' ? 'var(--success)' : 'var(--error)'
            }}
          >
            {notification.message}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
