// Enhanced search API
export const searchAPI = {
  searchAll: (query) => api.get(`/search?q=${encodeURIComponent(query)}`),
  searchNotes: (query) => api.get(`/search/notes?q=${encodeURIComponent(query)}`),
  searchCourses: (query) => api.get(`/search/courses?q=${encodeURIComponent(query)}`)
};
